mapper.select:
select user_id, user_nickname
from user u join(select user_id2, is_friend
                 from friend
                 where user_id = #{user_id}) f on u.user_id = f.user_id2
where is_friend = 1
union all
select user_id, user_nickname
from user u join friend using(user_id)
where user_id2 = #{user_id}
and is_friend = 1;
